WorldTree.LastScript:
ClearAll()
//Number of converter layers
var Layers = 4
//Material indexes
var Gas = 0  //it is R134A
var Glass = 1
var B4C = 2
//Size of plates in X and Y
var XYsize = 10
//Thickness
var WindThick = 0.5
var GasGap = 0.35
var ConverterThick = 0.002 
var Zstep = WindThick + GasGap + ConverterThick 
//offset
var Z0 = 1

for (var i=0; i<Layers+1; i++)
{
  var z = Z0 + Zstep*i

  Box("Lwind"+i,     XYsize,XYsize, WindThick, Glass, "World", 0,0, z, 0,0,0)
    SetLine("Lwind"+i, 4, 1, 1)
  if (i == Layers) break;
  Box("Converter"+i, XYsize,XYsize, ConverterThick, B4C,   "World", 0,0, z+0.5*WindThick+0.5*ConverterThick, 0,0,0)
    SetLine("Converter"+i, 1, 1, 1)  
  //Box("Gas"+i,           XYsize,XYsize, GasGap,              Gas,   "World", 0,0, z+0.5*WindThick+ConverterThick+0.5*GasGap, 0,0,0)
  //  SetLine("Gas"+i, 2, 1, 1)  
}
